import React from 'react'

const defaultProps = {
    backgroundImage: "https://mdbootstrap.com/img/Photos/Horizontal/Work/4-col/img%20%2814%29.jpg",
    title: "",
    description: "",
    service: ""
}

export default class ReferentieCard extends React.Component {
    render () {
        return (
            <div className="my-3 card card-image mb-3" style={{height:"200px", overflow:"hidden", borderRadius: "4px", backgroundImage: `url('${!this.props.backgroundImage ? defaultProps.backgroundImage : this.props.backgroundImage}')`, color: '#000000'}}>
                <div class="text-white text-center d-flex align-items-center rgba-black-strong py-3 px-3">
                    <div>
                        <h5 class="pink-text"><i class="fas fa-chart-pie"></i> {this.props.service}</h5>
                        <h3 class="card-title pt-2"><strong>{this.props.title}</strong></h3>
                        <p>{this.props.description}</p>
                    </div>
                </div>


                {/*<div className="text-white text-center d-flex align-items-center img-gradient-overlay py-5 px-4">*/}
                {/*    <h5 className="orange-text"><i className="fas fa-desktop"></i> {this.props.service}</h5>*/}
