 import Layout from '../layouts/Main'
import { postContact } from '../api/contact/index'
import {Formik, Field, FieldProps} from "formik"

import {TextField, Checkbox, CheckboxGroup, RadioButton, RadioButtonGroup} from "../components/fields/InputField"

import IntroBanner from "../components/IntroBanner";

const initialFormFields = {pets: [], contactpersoon: '', emailadres: '', bedrijfsnaam: '', telefoonnummer: '', uw_bericht: '', select: "", radio: "", checkbox: false};

const testArray = {a: 1, b: 2, c: 3}

const Page = ({}) => {
    return <Layout>
        <IntroBanner title="contact"/>
        <div className="container">
            <div className="row">
                <div className="col-md-9">
                    <h2>neem contact met ons op</h2>
                    <p>Heeft u nog vragen of opmerkingen, vul dan hieronder het contactformulier in.
                        Voor vragen of opmerkingen met spoed mail dan naar helpdesk@xxxxx.nl.</p>
                    <Formik onSubmit={(data, actions) => {
                            actions.setSubmitting(true);
                            postForm(data, actions)
                        }}
                        initialValues={initialFormFields}>

                        {({values, errors, touched, setFieldValue, setFieldTouched, handleChange, handleBlur, handleSubmit, isSubmitting}) => <div>
                            <form onSubmit={handleSubmit} className="needs-validation">


                                <TextField
                                    type="text"
                                    name="contactpersoon"
                                    label="contactpersoon" 
                                    error={errors.select}
                                    value={values.select}
                                />

                                {/*<TextField*/}
                                {/*    id="contactpersoon"*/}
                                {/*    label="Which of these?"*/}
                                {/*    value={values.pets}*/}
                                {/*    error={errors.pets}*/}
                                {/*    name="contactpersoon"*/}
                                {/*    touched={touched.pets}*/}
                                {/*    onChange={setFieldValue}*/}
                                {/*    onBlur={setFieldTouched}*/}
                                {/*/>*/}

                                <CheckboxGroup
                                    id="pets"
                                    label="Which of these?"
                                    value={values.pets}
                                    error={errors.pets}
                                    touched={touched.pets}
                                    onChange={setFieldValue}
                                    onBlur={setFieldTouched}
                                >
                                    {Object.keys(testArray).map(function(name, index) {
                                        return (
                                            <Field
                                                key={index}
                                                component={Checkbox}
                                                id={name}
                                                label={name}
                                            />
                                        )
                                    })}
                                </CheckboxGroup>

                                <RadioButtonGroup
                                    id="radio"
                                    label="One of these please"
                                    value={values.radio}
                                    error={errors.radio}
                                    touched={touched.radio}
                                >
                                    {Object.keys(testArray).map(function(name, index) {
                                        return (
                                            <Field
                                                key={index}
                                                component={RadioButton}
                                                name="radio"
                                                id={name}
                                                label={name}
                                            />
                                        )
                                    })}
                                </RadioButtonGroup>


                                {/*<Field*/}
                                {/*    label="contactpersoon"*/}
                                {/*    type="text"*/}
                                {/*    name="contactpersoon"*/}
                                {/*    value={values.contactpersoon}*/}
                                {/*    as={InputField}*/}
                                {/*/>*/}

                                {/*<InputField*/}
                                {/*    type="text"*/}
                                {/*    name="contactpersoon"*/}
                                {/*    label="contactpersoon"*/}
                                {/*    error={errors.contactpersoon}*/}
                                {/*    value={values.contactpersoon} />*/}

                                {/*<InputField*/}
                                {/*    type="email"*/}
                                {/*    name="emailadres"*/}
                                {/*    label="emailadres"*/}
                                {/*    error={errors.emailadres}*/}
                                {/*    value={values.emailadres} />*/}

                                {/*<InputField*/}
                                {/*    type="text"*/}
                                {/*    name="bedrijfsnaam"*/}
                                {/*    label="bedrijfsnaam"*/}
                                {/*    error={errors.bedrijfsnaam}*/}
                                {/*    value={values.bedrijfsnaam} />*/}

                                {/*<InputField*/}
                                {/*    type="select"*/}
                                {/*    name="select"*/}
                                {/*    label="select"*/}
                                {/*    values={testArray}*/}
                                {/*    error={errors.select}*/}
                                {/*    value={values.select} />*/}

                                {/*<InputField*/}
                                {/*    type="radio"*/}
                                {/*    name="radio"*/}
                                {/*    label="radio"*/}
                                {/*    values={testArray}*/}
                                {/*    error={errors.radio}*/}
                                {/*    value={values.radio} />*/}

                                {/*<InputField*/}
                                {/*    type="checkbox"*/}
                                {/*    name="checkbox"*/}
                                {/*    label="checkbox"*/}
                                {/*    values={testArray}*/}
                                {/*    error={errors.checkbox}*/}
                                {/*    value={values.checkbox} />*/}

                                {/*<InputField*/}
                                {/*    type="textarea"*/}
                                {/*    name="uw_bericht"*/}
                                {/*    label="uw_bericht"*/}
                                {/*    error={errors.uw_bericht}*/}
                                {/*    value={values.uw_bericht} />*/}

                                {/*<Field*/}
                                {/*    type="checkbox"*/}
                                {/*    name="pets"*/}
                                {/*    label="checkbox"*/}
                                {/*    values={testArray}*/}
                                {/*    as={InputField} />*/}

                                <pre>{JSON.stringify(values, null, 2)}</pre>
                                {/*ERRORS:*/}
                                {/*<pre>{JSON.stringify(errors, null, 2)}</pre>*/}
                                {/*TOUCHED:*/}
                                {/*<pre>{JSON.stringify(touched, null, 2)}</pre>*/}
                                {/*<div className="form-group">*/}
                                {/*    <label>uw_bericht</label>*/}
                                {/*    <input  type="text"*/}
                                {/*            name="uw_bericht"*/}
                                {/*            onChange={handleChange}*/}
                                {/*            // onBlur={handleBlur}*/}
                                {/*            value={values.uw_bericht}*/}
                                {/*            className={`form-control ${errors.uw_bericht && touched.uw_bericht ? 'is-invalid' : ''}`}*/}
                                {/*    />*/}
                                {/*    {errors.uw_bericht && touched.uw_bericht &&*/}
                                {/*        <div style={{color: "red"}} className="invalid-feedback">{errors.uw_bericht}</div>*/}
                                {/*    }*/}
                                {/*</div>*/}

                                <button type="submit" disabled={isSubmitting}>Send</button>
                                <input type="reset" value="Clear" />
                            </form>
                        </div>}

                    </Formik>
                </div>
                <div className="col-md-3">

                </div>
            </div>
        </div>
    </Layout>
}

function postForm(data, actions) {
    return postContact(data)
        .then((data) => {
            if(typeof data.errors !== 'undefined'){
                Object.keys(data.errors).forEach(function (item) {
                    actions.setFieldError(item, data.errors[item][0])
                });
                return data.errors;
            }else if (data.status == '200'){
                console.log('messages is ontvangen', data)
            }else {
                console.log('something went wrong')
                return {}
            }
        })
        .finally(() => {
            actions.setSubmitting(false)
        })
}

export default Page
