import fetch from 'isomorphic-fetch'

export function postContact (body) {
    console.log('body: ', JSON.stringify(body), body);

    return fetch("http://localhost:3000/test/api/auth/contact", {
        method: 'POST',
        headers: {
            'Accept':'application/json',
            "Content-Type": "application/json",
        },
        cache: 'default',
        mode: 'cors',
        body: JSON.stringify(body)
    }).then(function (response) {
        // var apiResponse;
        // if (response.status == 422) {
        //     apiResponse = response.errors;
        // }
        // console.log('apiResponse', apiResponse);
        // console.log('test 1 hier is he tnu', response.status);
        return response.json();
    })
    .then(function (json) {
        // console.log('test 2', JSON.stringify(json));
        return json;
    }).catch(function(err){
        console.log(err);
        return err;
    });
}
