import React from "react";
import classNames from "classnames";
import {Field} from "formik"

// Input feedback
const InputFeedback = ({ error }) => error ? <div style={{color: "red"}} className={classNames("input-feedback")}>{error}</div> : null

// Checkbox input
export const Checkbox = ({
  field: { name, value, onChange, onBlur },
  form: { errors, touched },
  id,
  label,
  className,
  ...props
}) => {
    return (
        <div className="custom-control custom-checkbox">
            <input
                name={name}
                id={id}
                type="checkbox"
                value={value}
                checked={value}
                onChange={onChange}
                onBlur={onBlur}
                className="custom-control-input"
            />
            <label htmlFor={id} className="custom-control-label">{label}</label>
            {touched[name] && <InputFeedback error={errors[name]} />}
        </div>
    );
};


// Checkbox group
export class CheckboxGroup extends React.Component {
    constructor(props) {
        super(props);
    }

    handleChange = event => {
        const target = event.currentTarget;
        let valueArray = [...this.props.value] || [];

        if (target.checked) {
            valueArray.push(target.id);
        } else {
            valueArray.splice(valueArray.indexOf(target.id), 1);
        }

        this.props.onChange(this.props.id, valueArray);
    };

    handleBlur = () => {
        // take care of touched
        this.props.onBlur(this.props.id, true);
    };

    render() {
        const { value, error, touched, label, className, children } = this.props;

        const classes = classNames(
            "input-field",
            "form-group",
            {
                "is-success": value || (!error && touched), // handle prefilled or user-filled
                "is-error": !!error && touched
            },
            className
        );

        return (
            <div className={classes}>
                <label>{label}</label>
                {React.Children.map(children, child => {
                    return React.cloneElement(child, {
                        field: {
                            value: value.includes(child.props.id),
                            onChange: this.handleChange,
                            onBlur: this.handleBlur
                        }
                    });
                })}
                {touched && <InputFeedback error={error} />}
             </div>
        );
    }
}

// Radio input
export const RadioButton = ({
    field: { name, value, onChange, onBlur },
    id,
    label,
    className,
    ...props
}) => {
    return (
        <div className="custom-control custom-radio">
            <input
                name={name}
                id={id}
                type="radio"
                value={value}
                checked={value}
                onChange={onChange}
                onBlur={onBlur}
                className="custom-control-input"
                {...props}
            />
            <label htmlFor={id} className="custom-control-label">
                {label}
            </label>
        </div>
    );
};

// Radio group
export const RadioButtonGroup = ({
  value,
  error,
  touched,
  id,
  label,
  className,
  children
}) => {
    const classes = classNames(
        "input-field",
        {
            "is-success": value || (!error && touched), // handle prefilled or user-filled
            "is-error": !!error && touched
        },
        className
    );

    return (
        <div className={classNames("form-group")}>
            <label>{label}</label>
            {children}
            {touched && <InputFeedback error={error} />}
        </div>
    );
};

export const SelectField = (props) => {
    return (
        <Field type="select" component={InputField} {...props} />
    )
}

export const TextField = (props) => {
    return (
        <Field type="text" component={InputField} {...props} />
    )
}

export const TextareaField = (props) => {
    return (
        <Field type="textarea" component={InputField} {...props} />
    )
}

export class InputField extends React.Component {
    constructor(props) {
        super(props);
    }

    handleChange = event => {
        const {
            field: { name },
            form: { handleChange }
        } = this.props;

        handleChange(event);
    }

    renderSwitch(type) {
        switch (type) {
            case 'textarea':
                return <TextareaInput {...this.props} onChange={this.handleChange} />
            case 'text':
                return <TextInput {...this.props} onChange={this.handleChange} />
            case 'email':
                return <EmailInput {...this.props} onChange={this.handleChange} />
            default:
                return
        }
    }

    render() {
        const {label} = this.props;

        const inputProps = Object.assign({}, this.props);
        delete inputProps.form;
        delete inputProps.field;
        delete inputProps.error;
        delete inputProps.values;

        console.log(this.props.type)

        return (
            <div className="form-group">
                <style>{`
                    
                `}</style>
                <label>{label}</label>
                {this.renderSwitch(this.props.type)}
                {/*<TextareaInput {...this.props} onChange={this.handleChange} />*/}
                {/*<inputComponent />*/}

                {this.props.error && <InputFeedback error={this.props.error} />}
            </div>
        );
    }
}

