export const TextareaInput = (props) => {
    return (
        <textarea
            className={`form-control${props.error ? ' is-invalid' : ''}`}
            onChange={props.onChange}
            id={props.label}
            {...props}
        ></textarea>
    )
}

export const EmailInput = (props) => {
    return (
        <input
            className={`form-control${props.error ? ' is-invalid' : ''}`}
            onChange={props.onChange}
            id={props.label}
            {...props}
        />
    )
}

export const TextInput = (props) => {
    return (
        <input
            className={`form-control${props.error ? ' is-invalid' : ''}`}
            onChange={props.onChange}
            id={props.label}
            {...props}
        />
    )
}


