import fetch from 'isomorphic-fetch'

export function postContact (body, actions) {
    console.log('body: ', JSON.stringify(body), body);

    return fetch("http://localhost:3000/test/api/auth/contact", {
        method: 'POST',
        headers: {
            'Accept':'application/json',
            "Content-Type": "application/json",
        },
        cache: 'default',
        mode: 'cors',
        body: JSON.stringify(body)
    }).then(response => {
        return response;
    })
    .catch(error => {
        /* If your server returns something like a 422 when the
           validation fails it will move to the catch part. Here
           you can use the actions.setFieldError to set the errors
           in your form.
        */
        error.response.data.forEach(error => {
            actions.setFieldError(error.field, error.message)
        })
        return error;
    })
    .finally(() => actions.setSubmitting(false))

