 import { Link } from '../routes'

const defaultProps = {
    title: "This is the card title",
    subText: "test test",
    iconClass: "fas fa-store-alt",
    backgroundImage: "",
    // backgroundImage: "https://mdbootstrap.com/img/Photos/Horizontal/Work/4-col/img%20%2814%29.jpg",
}

export default class Container extends React.Component {
    render () {
        return (
            <div className="py-3 col-md-4 animated fadeIn delay-2s wow">
                <Link route={this.props.route}>
                    <div class="card " style={{height:"100%"}}>
                        <div class="card-body text-center">
                            <span className="blue-text" style={{ padding: "50px"}} >
                                <i className={!this.props.iconClass ? defaultProps.iconClass : this.props.iconClass} style={{fontSize:"60px", marginBottom: "20px"}}></i>
                            </span>
                            <br/>
                            <h4 class="card-title">{!this.props.title ? defaultProps.title : this.props.title}</h4>
                            <p class="card-text">{!this.props.subText ? defaultProps.subText : this.props.subText}</p>
                            <a class="btn center-block btn-blue fixed-bottom position-relative">Lees meer</a>
                        </div>
                    </div>
                </Link>
            </div>
        )
    }
}


