import fetch from 'isomorphic-fetch'

export function postContact (body) {
    return fetch(`${process.env.API_PREFIX}/contact`, {
        method: 'POST',
        headers: {
            'Accept':'application/json',
            "Content-Type": "application/x-www-form-urlencoded",
        },
        cache: 'default',
        body: body
    })
    .then((resp) => {
        return resp.json()
    })
}
