import fetch from 'isomorphic-fetch'

const apiDomain = "https://api-bedrijfswebsite.mediaverse-dev.nl"
const apiPrefix = "/api/api/auth"

export function postContact (body) {
    return fetch(`https://api-bedrijfswebsite.mediaverse-dev.nl/api/api/auth/contact`, {
        method: 'POST',
        headers: {
            'Accept':'application/json',
            "Content-Type": "application/json",
            "Access-Control-Allow-Origin": "*",
        },
        cache: 'default',
        mode: 'cors',
        body: JSON.stringify(body)
    })
    .then((resp) => {
        return resp.json()
    })
}
