import React from "react";
import Layout from '../layouts/Main'
import IntroBanner from "../components/IntroBanner"
 import {Field} from "formik";
import {InputField} from "../components/fields/InputField";
import ReferenceCard from "../components/ReferenceCard";

import { getReferences } from 'api/reference'

function Page({data}) {

    return <Layout>

        <IntroBanner title="Referenties"/>

        <div className="container py-5">
            <div className="row">
                {posts.map(p => <Post key={p.title} post={p} />)}
                {data.map(function(key, index) {
                    return (
                        <div className="col-md-4">
                            <ReferenceCard
                                key={key.id}
                                title={key.title}
                                service={key.service}
                                description={key.service}
                                backgroundImage={key.service}
                            />
                        </div>
                    )
                })}

            </div>
        </div>

    </Layout>
}

Page.getInitialProps = async ({ req }) => {
    const res = await getReferences()
    const json = await res.json()
    return { data: json }
}

export default Page
