import { withRouter } from 'next/router'
import Layout from '../layouts/Main'
import { postContact } from '../api/contact/index'
import {Formik, Field, FieldProps} from "formik"
import fetch from "isomorphic-fetch";

import InputField from "../components/fields/InputField"

import IntroBanner from "../components/IntroBanner";

const initialFormFields = {contactpersoon: '', emailadres: '', bedrijfsnaam: '', telefoonnummer: '', uw_bericht: ''};

const Page = ({}) => {

    return <Layout>

        <IntroBanner title="contact"/>
        <div className="container">
            <div className="row">
                <div className="col-md-9">
                    <h2>neem contact met ons op</h2>
                    <p>Heeft u nog vragen of opmerkingen, vul dan hieronder het contactformulier in.
                        Voor vragen of opmerkingen met spoed mail dan naar helpdesk@xxxxx.nl.</p>
                    <Formik onSubmit={(data, actions) => {
                            actions.setSubmitting(true);
                            postForm(data, actions)
                        }}
                        initialValues={initialFormFields}>

                        {({values, errors, touched, handleChange, handleBlur, handleSubmit, isSubmitting}) => <div>
                            <form onSubmit={handleSubmit} className="needs-validation">
                                <InputField
                                    type="text"
                                    name="contactpersoon"
                                    label="contactpersoon"
                                    error={errors.contactpersoon}
                                    value={values.contactpersoon} />

                                <InputField
                                    type="email"
                                    name="emailadres"
                                    label="emailadres"
                                    error={errors.emailadres}
                                    value={values.emailadres} />

                                <InputField
                                    type="text"
                                    name="bedrijfsnaam"
                                    label="bedrijfsnaam"
                                    error={errors.bedrijfsnaam}
                                    value={values.bedrijfsnaam} />

                                <InputField
                                    type="text"
                                    name="uw_bericht"
                                    label="uw_bericht"
                                    error={errors.uw_bericht}
                                    value={values.uw_bericht} />

                                <InputField
                                    type="textarea"
                                    name="uw_bericht"
                                    label="uw_bericht"
                                    error={errors.uw_bericht}
                                    value={values.uw_bericht} />

                                {/*<div className="form-group">*/}
                                {/*    <label>uw_bericht</label>*/}
                                {/*    <input  type="text"*/}
                                {/*            name="uw_bericht"*/}
                                {/*            onChange={handleChange}*/}
                                {/*            // onBlur={handleBlur}*/}
                                {/*            value={values.uw_bericht}*/}
                                {/*            className={`form-control ${errors.uw_bericht && touched.uw_bericht ? 'is-invalid' : ''}`}*/}
                                {/*    />*/}
                                {/*    {errors.uw_bericht && touched.uw_bericht &&*/}
                                {/*        <div style={{color: "red"}} className="invalid-feedback">{errors.uw_bericht}</div>*/}
                                {/*    }*/}
                                {/*</div>*/}

                                <button type="submit" disabled={isSubmitting}>Send</button>
                                <input type="reset" value="Clear" />
                            </form>
                        </div>}

                    </Formik>
                </div>
                <div className="col-md-3">

                </div>
            </div>
        </div>
    </Layout>
}

function postForm(data, actions) {
    return postContact(data)
        .then((data) => {
            if(typeof data.errors !== 'undefined'){
                Object.keys(data.errors).forEach(function (item) {
                    actions.setFieldError(item, data.errors[item][0])
                });
                return data.errors;
            }else if (data.status == '200'){
                console.log('messages is ontvangen', data)
            }else {
                console.log('something went wrong')
                return {}
            }
        })
        .finally(() => {
            actions.setSubmitting(false)
        })
}

export default Page
