import { withRouter } from 'next/router'
import Layout from '../layouts/Main'
import { postContact } from '../api/contact/index'
import {Formik, FieldProps} from "formik"
import fetch from "isomorphic-fetch";

//
// Page.getInitialProps = async ({ query }) => {
//     const res = await getCategories()
//     const json = await res.json()
//
//     return { categories: json.data }
// }

const Page = ({}) => {

    return <Layout>
        <div className="jumbotron">
            <div className="container">
                <h1 className="display-3 text-center">Contact</h1>
            </div>
        </div>

        <div className="container">
            <div className="row">
                <div className="col-md-9">
                    <h2>neem contact met ons op</h2>
                    <p>Heeft u nog vragen of opmerkingen, vul dan hieronder het contactformulier in.
                        Voor vragen of opmerkingen met spoed mail dan naar helpdesk@xxxxx.nl.</p>
                    <Formik
                        onSubmit={async (data, actions) => {

                            const text = fetch("http://localhost:3000/test/api/auth/contact", {
                                method: 'POST',
                                headers: {
                                    'Accept':'application/json',
                                    "Content-Type": "application/json",
                                },
                                cache: 'default',
                                mode: 'cors',
                                body: JSON.stringify(data)
                            })
                            .then((response) => {
                                actions.setFieldError('general', 'test test test')

                                const error = response;

                                console.log('test test test:', error);


                                return error.json()
                            })
                            .then((data) => {
                                console.log('Success:', data);
                            })
                            .catch((error) => {
                                console.error('Error:', error);
                            })
                            .finally(() => {
                                actions.setSubmitting(false)
                            })

                            text().then(data => {
                                response.json({ message: 'Request received!', data })
                            })
                            console.log('text text', text)

                            // console.log('actions', actions)
                            // console.log('data', data)
                            // const res = await postContact(data,actions)
                            // const json = await res
                            // console.log('res', JSON.stringify(res));
                            // console.log('content', JSON.stringify(json));
                            // // actions.setFieldError('content', content);
                            // actions.setFieldError('contactpersoon', 'test test test');
                            // actions.setFieldError('emailadres', 'test test test');
                        }}
                        initialValues={{contactpersoon: '', emailadres: '', bedrijfsnaam: '', telefoonnummer: '', uw_bericht: ''}}
                    >
                        {({values, errors, touched, handleChange, handleBlur, handleSubmit, isSubmitting}) => <div>
                            <form onSubmit={handleSubmit}>

                                {/*<Field name="email" component={InputField} />*/}

                                <div className="form-group">
                                    <label>contactpersoon</label>

                                    <input
                                        type="text"
                                        name="contactpersoon"
                                        onChange={handleChange}
                                        onBlur={handleBlur}
                                        value={values.contactpersoon}
                                        className="form-control"
                                    />
                                    <div className="valid-tooltip">
                                        {/*{JSON.stringify(handleSubmit)}*/}
                                        {errors.contactpersoon && touched.contactpersoon ? (
                                            <div> asdadasd</div>
                                        ) : null}
                                    </div>
                                </div>
                                <div className="form-group">
                                    <label>Email address</label>

                                    <input
                                        type="email"
                                        name="emailadres"
                                        onChange={handleChange}
                                        onBlur={handleBlur}
                                        value={values.emailadres}
                                        className="form-control"
                                    />
                                    {errors.emailadres && touched.emailadres && <div>{errors.emailadres}</div>}

                                    <div className="valid-tooltip">
                                        Looks good!
                                    </div>
                                </div>
                                <div className="form-group">
                                    <label>bedrijfsnaam</label>
                                    <input
                                        type="text"
                                        name="bedrijfsnaam"
                                        onChange={handleChange}
                                        onBlur={handleBlur}
                                        value={values.bedrijfsnaam}
                                        className="form-control"
                                    />                                    <div className="valid-tooltip">
                                        Looks good!
                                    </div>
                                </div>
                                <div className="form-group">
                                    <label>telefoonnummer</label>
                                    <input
                                        type="text"
                                        name="telefoonnummer"
                                        onChange={handleChange}
                                        onBlur={handleBlur}
                                        value={values.telefoonnummer}
                                        className="form-control"
                                    />
                                    <div className="valid-tooltip">
                                        Looks good!
                                    </div>
                                </div>
                                <div className="form-group">
                                    <label>uw_bericht</label>
                                    <input  type="text"
                                            name="uw_bericht"
                                            onChange={handleChange}
                                            onBlur={handleBlur}
                                            value={values.uw_bericht}
                                            className="form-control"
                                    />
                                    <div className="valid-tooltip">
                                    </div>
                                </div>
                                {JSON.stringify(values, errors, touched, handleChange, handleBlur, handleSubmit, isSubmitting)}

                                <button type="submit" >Send</button>
                                <input type="reset" value="Clear" />
                            </form>
                        </div>}

                    </Formik>



                </div>
                <div className="col-md-3">

                </div>
            </div>
        </div>

    </Layout>
}

export default Page
