 import { Link } from '../routes'

const defaultProps = {
    title: "This is the card title",
    subText: "test test",
    backgroundImage: "",
    // backgroundImage: "https://mdbootstrap.com/img/Photos/Horizontal/Work/4-col/img%20%2814%29.jpg",
}

export default class Container extends React.Component {
    render () {
        return (
            <div class="py-3">
                <div class="card">
                    {/*<img class="card-img-top" src="https://mdbootstrap.com/img/Photos/Others/images/43.jpg" alt="Card image cap" />*/}
                    <div class="card-body">
                        <span className="pink-text text-center">
                            <i className="fas fa-chart-pie"></i>
                        </span>
                        <h4 class="card-title">{!this.props.title ? defaultProps.title : this.props.title}</h4>
                        <p class="card-text">{!this.props.subText ? defaultProps.subText : this.props.subText}</p>
                        <a href="#" class="btn btn-primary">Button</a>
                    </div>
                </div>
            </div>
        )
    }
}


