import Layout from '../layouts/Main'
import { postContact } from '../api/contact/index'
import {Formik, Field, FieldProps} from "formik"

import {SelectField, TextField, Checkbox, CheckboxGroup, RadioButton, RadioButtonGroup} from "../components/fields/InputField"

import IntroBanner from "../components/IntroBanner";

const initialFormFields = {pets: [], contactpersoon: '', emailadres: '', bedrijfsnaam: '', telefoonnummer: '', uw_bericht: '', select: "", radio: "", checkbox: false};

const ServiceTypes = {
    "onderhoud van uw website": 1,
    "websoftware een maatwerk systeem special gemaakt op uw behoeftes": 2,
    c: 3
}

const SLAs = {
    "hosting voor uw applicatie('s)": 1,
    "wilt u een zakelijk telefoon nummer": 2,
    "": 3
}

const Page = ({}) => {

    const onSubmitForm = (data, actions) => {
        actions.setSubmitting(true);
        postForm(data, actions)
    }

    const formEvents = {values, errors, touched, setFieldValue, setFieldTouched, handleChange, handleBlur, handleSubmit, isSubmitting};

    return <Layout>
        <IntroBanner title="offerte aanvraag"/>
        <div className="container">
            <div className="row">
                <div className="col-md-9">

                    <h2>offerteformulier</h2>
                    <Formik onSubmit={onSubmitForm} initialValues={initialFormFields}>

                        {(formEvents) => <div>
                            <form onSubmit={handleSubmit} className="needs-validation">

                                <TextField
                                    name="contactpersoon"
                                    label="contactpersoon"
                                    error={errors.contactpersoon}
                                    value={values.contactpersoon}
                                />
                                <TextField
                                    type="email"
                                    name="emailadres"
                                    label="emailadres"
                                    error={errors.emailadres}
                                    value={values.emailadres} />
                                <TextField
                                    type="text"
                                    name="bedrijfsnaam"
                                    label="bedrijfsnaam"
                                    error={errors.bedrijfsnaam}
                                    value={values.bedrijfsnaam} />

                                <CheckboxGroup
                                    id="pets"
                                    label="naar welke dienst bent u opzoek?"
                                    value={values.pets}
                                    error={errors.pets}
                                    touched={touched.pets}
                                    onChange={setFieldValue}
                                    onBlur={setFieldTouched}
                                >
                                    {Object.keys(ServiceTypes).map(function(name, index) {
                                        return (
                                            <Field
                                                key={index}
                                                component={Checkbox}
                                                id={name}
                                                label={name}
                                            />
                                        )
                                    })}
                                </CheckboxGroup>

                                <CheckboxGroup
                                    id="pets"
                                    label="maandelijkse diensten"
                                    value={values.pets}
                                    error={errors.pets}
                                    touched={touched.pets}
                                    onChange={setFieldValue}
                                    onBlur={setFieldTouched}
                                >
                                    {Object.keys(SLAs).map(function(name, index) {
                                        return (
                                            <Field
                                                key={index}
                                                component={Checkbox}
                                                id={name}
                                                label={name}
                                            />
                                        )
                                    })}
                                </CheckboxGroup>

                                <TextField
                                    type="textarea"
                                    name="opmerking"
                                    label="uw_bericht"
                                    error={errors.uw_bericht}
                                    value={values.uw_bericht} />

                                <button type="submit" className="btn-blue btn btn-block" disabled={isSubmitting}>Verzenden</button>

                                <pre>{JSON.stringify(values, null, 2)}</pre>


                            </form>
                        </div>}

                    </Formik>
                </div>
                <div className="col-md-3">

                </div>
            </div>
        </div>
    </Layout>
}

function postForm(data, actions) {
    return postContact(data)
        .then((data) => {
            if(typeof data.errors !== 'undefined'){
                Object.keys(data.errors).forEach(function (item) {
                    actions.setFieldError(item, data.errors[item][0])
                });
                return data.errors;
            }else if (data.status == '200'){
                console.log('messages is ontvangen', data)
            }else {
                console.log('something went wrong')
                return {}
            }
        })
        .finally(() => {
            actions.setSubmitting(false)
        })
}

export default Page
