import React from 'react'

const defaultProps = {
    backgroundImage: "https://mdbootstrap.com/img/Photos/Horizontal/City/6-col/img%20(47).jpg",
    title: "",
    description: ""
}

export default class ReferentieCard extends React.Component {
    // https://mdbootstrap.com/img/Photos/Horizontal/City/6-col/img%20(47).jpg
    render () {
        return (
            <div className="card card-image mb-3" style={{backgroundImage: `url('${!this.props.backgroundImage ? defaultProps.backgroundImage : this.props.backgroundImage}')`, color: '#000000'}}>
                <div
                    className="text-white text-center d-flex align-items-center img-gradient-overlay py-5 px-4">
                    <div>
                        <h5 className="orange-text">
                            <i className="fas fa-desktop"></i> {this.props.service}
                        </h5>
                        <h3 className="card-title pt-2"><strong>{this.props.title}</strong></h3>
                        <p>{this.props.description}</p>
                        <a className="btn btn-deep-orange waves-effect waves-light">
                            <i className="far fa-clone left"></i>
                            Bekijk project
                        </a>
                    </div>
                </div>
            </div>
        )
    }
}
