import { withRouter } from 'next/router'
import Layout from '../layouts/Main'
import { postContact } from '../api/contact/index'
import {Formik, Field, FieldProps} from "formik"
import fetch from "isomorphic-fetch";

const Page = ({}) => {

    return <Layout>
        <div className="jumbotron">
            <div className="container">
                <h1 className="display-3 text-center">Contact</h1>
            </div>
        </div>

        <div className="container">
            <div className="row">
                <div className="col-md-9">
                    <h2>neem contact met ons op</h2>
                    <p>Heeft u nog vragen of opmerkingen, vul dan hieronder het contactformulier in.
                        Voor vragen of opmerkingen met spoed mail dan naar helpdesk@xxxxx.nl.</p>
                    <Formik
                        onSubmit={(data, actions,) => {
                            actions.setSubmitting(true);

                            fetch("http://localhost:3000/test/api/auth/contact", {
                                method: 'POST',
                                headers: {
                                    'Accept':'application/json',
                                    "Content-Type": "application/json",
                                },
                                cache: 'default',
                                mode: 'cors',
                                body: JSON.stringify(data)
                            })
                            .then((response) => {

                                const error = response;

                                console.log('test test test:', error);
                                return error.json()
                            })
                            .then((data) => {
                                Object.keys(data.errors).forEach(function (item) {
                                    actions.setFieldError(item, data.errors[item][0])
                                });

                                // arr.push(data.errors)
                                console.log('Successssssssss:', data.errors);
                                return data.errors;
                            })
                            .catch((error) => {
                                console.error('Error:', error);
                            })
                            .finally(() => {
                                actions.setSubmitting(false)
                            })

                        }}
                        initialValues={{contactpersoon: '', emailadres: '', bedrijfsnaam: '', telefoonnummer: '', uw_bericht: ''}}
                    >
                        {({values, errors, touched, handleChange, handleBlur, handleSubmit, isSubmitting}) => <div>
                            <form onSubmit={handleSubmit} className="needs-validation" novalidate>

                                {/*<Field name="email" component={InputField} />*/}

                                <div className="form-group">
                                    <label>contactpersoon</label>
                                    <Field
                                        type="text"
                                        name="contactpersoon"
                                        onChange={handleChange}
                                        onBlur={handleBlur}
                                        value={values.contactpersoon}
                                        className="form-control"
                                    />
                                    {errors.contactpersoon && touched.contactpersoon && <div style={{color: "red"}} className="invalid-feedback">{errors.contactpersoon}</div>}
                                </div>

                                <div className="col-md-4 mb-3">
                                    <label htmlFor="validationTooltip01">contactpersoon</label>
                                    <input type="text" className="form-control" id="validationTooltip01"
                                           placeholder="First name" value=""   />
                                        <div className="valid-tooltip">
                                            Looks good!
                                        </div>
                                </div>

                                <div className="form-group">
                                    <label>Email address</label>
                                    <Field
                                        type="email"
                                        name="emailadres"
                                        onChange={handleChange}
                                        onBlur={handleBlur}
                                        value={values.emailadres}
                                        className="form-control"
                                    />
                                    {errors.emailadres && touched.emailadres && <div>{errors.emailadres}</div>}

                                    <div className="valid-tooltip">
                                        Looks good!
                                    </div>
                                </div>
                                <div className="form-group">
                                    <label>bedrijfsnaam</label>
                                    <input
                                        type="text"
                                        name="bedrijfsnaam"
                                        onChange={handleChange}
                                        onBlur={handleBlur}
                                        value={values.bedrijfsnaam}
                                        className="form-control"
                                    />                                    <div className="valid-tooltip">
                                        Looks good!
                                    </div>
                                </div>
                                <div className="form-group">
                                    <label>telefoonnummer</label>
                                    <input
                                        type="text"
                                        name="telefoonnummer"
                                        onChange={handleChange}
                                        onBlur={handleBlur}
                                        value={values.telefoonnummer}
                                        className="form-control"
                                    />
                                    <div className="valid-tooltip">
                                        Looks good!
                                    </div>
                                </div>
                                <div className="form-group">
                                    <label>uw_bericht</label>
                                    <input  type="text"
                                            name="uw_bericht"
                                            onChange={handleChange}
                                            onBlur={handleBlur}
                                            value={values.uw_bericht}
                                            className="form-control"
                                    />
                                    <div className="valid-tooltip">
                                    </div>
                                </div>
                                {JSON.stringify(values, errors, touched, handleChange, handleBlur, handleSubmit, isSubmitting)}

                                <button type="submit" disabled={isSubmitting}>Send</button>
                                <input type="reset" value="Clear" />
                            </form>
                        </div>}

                    </Formik>



                </div>
                <div className="col-md-3">

                </div>
            </div>
        </div>

    </Layout>
}

Page.getInitialProps = async (text) => {
    const res = await postContact()
    const json = await res.json()
    console.log('text', text)
    return { categories: json.data }
}

export default Page
