export const TextareaInput = (props) => {
    console.log("props", props)
    return (
        <textarea
            className={`form-control${props.error ? ' is-invalid' : ''}`}
            onChange={props.onChange}
            id={props.label}
            {...props}
        ></textarea>
    )
}

export const TextInput = (props) => {
    console.log("props", props)
    return (
        <input
            className={`form-control${props.error ? ' is-invalid' : ''}`}
            onChange={props.onChange}
            id={props.label}
            {...props}
        />
    )
}


