import React from 'react'

const defaultProps = {
    backgroundImage: "https://mdbootstrap.com/img/Photos/Others/architecture.jpg" ,
}

export default class Container extends React.Component {
    render () {
        return (
            <div className="view mb-5">
                {/*<div className="view mb-4">*/}
                    <img src="https://www.intrige.eu/wp-content/uploads/2019/02/Hosting-compressor.jpg" style={{backgroundImage: `url('${!this.props.backgroundImage ? defaultProps.backgroundImage : this.props.backgroundImage}')`, height:"230px"}} className="img-fluid" alt="" />
                    <div className="mask blue-gradient-rgba flex-center">
                        <p className="text-center text-white">
                            <h2 className="dis play-3 text-white text-center">{this.props.title }</h2>
                        </p>
                    </div>
                {/*</div>*/}
            </div>
        )
    }
}
