import React, { Component } from 'react';
import { Field } from 'formik';

class TextInputWrappedInternal extends Component {
    constructor(props) {
        super(props);
    }

    handleChange = event => {
        const {
            field: { name },
            form: { handleChange }
        } = this.props;

        handleChange(event);
    };

    render() {
        const {field, label} = this.props;

        const inputProps = Object.assign({}, this.props);

        delete inputProps.form;
        delete inputProps.label;
        delete inputProps.field;
        delete inputProps.error;

        const map = { a: 1, b: 2, c: 3 };

        // const result = Object.keys(map).map((key) => map[key]);

        return (
            <div className="form-group">
                {this.props.label &&
                    <label htmlFor={this.props.id}>{label}</label>
                }
                {this.props.type == "text" &&
                    <input
                        onChange={this.handleChange} id={label} value={field.value} name={field.name} {...inputProps}
                        className={`form-control${this.props.error ? ' is-invalid' : ''}`}
                    />
                }
                {this.props.type == "textarea" &&
                    <textarea
                        onChange={this.handleChange} id={label} value={field.value} name={field.name} {...inputProps}
                        className={`form-control${this.props.error ? ' is-invalid' : ''}`}
                    />
                }
                {/*{this.props.type == "select" }*/}
                    {/*<select*/}
                    {/*    onChange={this.handleChange} id={label} value={field.value} name={field.name} {...inputProps}*/}
                    {/*    className={`form-control${this.props.error ? ' is-invalid' : ''}`}*/}
                    {/*>*/}
                    {/*    /!*<option value="test">test</option>*!/*/}
                    {/*</select>*/}


                {this.props.type == "checkbox" && typeof this.props.values !== "undefined" &&
                    Object.keys(map) .map(function(name, index){
                        return <div className="form-check" key={ index }>
                            <input className="form-check-input" type="checkbox" value={name} id={name+index} />
                            <label className="form-check-label" htmlFor="defaultCheck2">
                                {name}
                            </label>
                        </div>
                    })

                }
                {this.props.error &&
                    <div style={{color: "red"}} className="invalid-feedback">
                        {this.props.error}
                    </div>
                }
            </div>
        );
    }
}

export default props => <Field component={TextInputWrappedInternal} {...props} />;
