 import { Link } from '../routes'

const defaultProps = {
    title: "Een website is meer dan alleen een visitekaartje",
    subText: "test test",
    backgroundImage: "https://mdbootstrap.com/img/Photos/Horizontal/Work/4-col/img%20%2814%29.jpg",
}

export default class Container extends React.Component {
    render () {
        return (

            <div className="py-3">
                <div className="card card-image" style={{backgroundImage: `url('${!this.props.backgroundImage ? defaultProps.backgroundImage : this.props.backgroundImage}')`}}>
                    {/*<style jsx>{`  */}
                    {/*      .pink-text i{*/}
                    {/*        font-size: 70px !important;*/}
                    {/*      }*/}
                    {/*`}</style>*/}

                        <div className="text-white text-center d-flex align-items-center rgba-black-strong py-5 px-4">
                            <div>
                            <span className="pink-text"><i className="fas fa-chart-pie"></i></span>
                            <h3 className="card-title pt-2">
                                <strong>This is the card title</strong>
                            </h3>
                            <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Repellat fugi</p>
                            <a className="btn btn-pink">lees meer</a>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
