import React from "react";
import {Field} from "formik"

export default class InputField extends React.Component {
    field = props => {
        return (
            <div>
                {props.label && <label htmlFor={props.id}>{props.label}</label>}
                <Field
                    type="text"
                    name="contactpersoon"
                    // onChange={handleChange}
                    // onBlur={handleBlur}
                    value={values.contactpersoon}
                    className={`form-control ${props.errors.contactpersoon ? 'is-invalid' : ''}`}
                />
                {props.touched && props.error && <div className="text-danger">{props.error}</div>}
            </div>
        );
    }
    render() {

        const componentToRender = this.props.component;

        return <Field {...this.props} {...componentToRender} component={this.field} />;
    }
}
