                                    error={errors.checkboxGroup}
                                    touched={touched.checkboxGroup}
                                    onChange={setFieldValue}
                                    onBlur={setFieldTouched}
                                >
                                    <Field
                                        component={Checkbox}
                                        name="pets"
                                        id="checkbox1"
                                        label="Option 1"
                                    />
                                    <Field
                                        component={Checkbox}
                                        name="pets"
                                        id="checkbox2"
                                        label="Option 2"
                                    />
                                    <Field
                                        component={Checkbox}
                                        name="pets"
                                        id="checkbox3"
                                        label="Option 3"
                                    />
                                </CheckboxGroup>

                                {/*<Field*/}
                                {/*    label="contactpersoon"*/}
                                {/*    type="text"*/}
                                {/*    name="contactpersoon"*/}
                                {/*    value={values.contactpersoon}*/}
