import React, { Component } from 'react';
import { Field } from 'formik';
import PropTypes from 'prop-types';

class TextInputWrappedInternal extends Component {
    static propTypes = {
        field: PropTypes.shape().isRequired,
        form: PropTypes.shape().isRequired,
        label: PropTypes.string.isRequired
    };

    constructor(props) {
        super(props);
    }

    handleChange = event => {
        const {
            field: { name },
            form: { handleChange, setFieldTouched }
        } = this.props;

        handleChange(event);
    };

    render() {
        const {field, label} = this.props;
        console.log(this.props)
        return (
            <div className="form-group">
                {this.props.label &&
                    <label htmlFor={this.props.id}>{this.props.label}</label>
                }
                <input
                    onChange={this.handleChange} value={field.value} name={field.name} label={label} {...this.props}
                    className={`form-control ${this.props.error ? 'is-invalid' : ''}`}
                />
                {this.props.error &&
                    <div style={{color: "red"}} className="invalid-feedback">
                        {this.props.error}
                    </div>
                }
            </div>
        );
    }
}

export default props => <Field component={TextInputWrappedInternal} {...props} />;
