import React from "react";
import absoluteUrl from 'next-absolute-url'
import Layout from '../layouts/Main'
import IntroBanner from "../components/IntroBanner"
import ReferenceCard from "../components/ReferenceCard";

import { getReferences } from '../api/reference/index'

function Page({data}) {

    return <Layout>

        <IntroBanner title="Referenties"/>

        <div className="container pt-2 pb-5">
            <div className="row">
                {data.map(function(key, index) {
                    return (
                        <div className="col-md-4" key={index}>
                            <ReferenceCard
                                title={key.title}
                                service={key.service}
                                description={key.description}
                                backgroundImage={key.image}
                            />
                        </div>
                    )
                })}

            </div>
        </div>

    </Layout>
}

const fetchRelative = path => {
    const {origin} = absoluteUrl(req, 'localhost:3000');
    return fetch(`${origin}${path}`);
}

Page.getServerSideProps = async ({req, res}) => {
    const resp = await getReferences()
    const users = await resp.json();
    return {users}
}

// export async function getServerSideProps() {
//     const res = await getReferences()
//     const json = await res.json()
//     const data = json.data
//     return { props: { data  } }
// }

export default Page
